import React, { useState, FormEvent } from 'react'
import { Form, Button, Dropdown, Input } from 'semantic-ui-react'
import { Party } from "@daml/types"
import { User } from "@daml.js/create-daml-app"
import { useParty, useLedger } from "@daml/react"

type Props = {
  followers: Party[]
}

// Component for editting message to send to a follower

const MessageEdit: React.FC<Props> = ({followers}) => {
  const sender = useParty()
  const [receiver, setReceiver] = useState<string | undefined>()
  const [content, setContent] = useState('')
  const [isSubmitting, setIsSubmitting] = useState(false)
  const ledger = useLedger()

  const submitMessage = async (event: FormEvent) => {
    try {
      event.preventDefault()
      if (receiver === undefined) {
        return
      }
      setIsSubmitting(true)
      await ledger.exerciseByKey(User.User.SendMessage, receiver, {sender, content})
      setContent("")
    } catch (error) {
      alert(`Error sending message:\n${JSON.stringify(error)}`)
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <Form onSubmit={submitMessage}>
      <Dropdown
        selection
        className='test-select-message-receiver'
        placeholder='Select a follower'
        options={followers.map(follower => ({
          key: follower,
          text: follower,
          value: follower
        }))}
        value={receiver}
        onChange={event => setReceiver(event.currentTarget.textContent ?? undefined)}
      />
    <Input
      className='test-select-message-content'
      placeholder='Write a message'
      value={content}
      onChange={event => setContent(event.currentTarget.value)}
    />
    <Button
      fluid
      className='test-select-message-send-button'
      type='submit'
      disabled={isSubmitting || receiver === undefined || content === ''}
      loading={isSubmitting}
      content='Send'
    />
    </Form>
  )
}

export default MessageEdit
